# Buy
def buy_token(modes, ca):
    try:
        token = web3.toChecksumAddress(ca)
    except:
        print("Invalid address")
        return
    t = helpers.localtime()

    current_time = helpers.strftime("%H:%M:%S", t)
    print(current_time, "Buying", ca, "with wallets in modes:", modes)

    for mode in modes:
        t = threading.Thread(target=buy_token_from_mode(mode, ca))
        t.start()


def buy_token_from_mode(mode, ca):
    wallets = mode["wallets"]
    print(wallets)
    for wallet in wallets:
        t = threading.Thread(target=buy_token_from_wallet(wallet, ca))


def buy_token_from_wallet(wallet, ca):
    token = web3.toChecksumAddress(ca)

    address = wallet["address"]

    transaction = pancake_contract.functions.swapExactETHForTokens(
        0,
        [wbnb, token],
        address,
        (int(helpers.time()) + 10000)
    ).buildTransaction({
        "from": address,
        "value": web3.toWei(wallet["buy_amount"], "ether"),
        "gas": int(wallet["gas_limit"]),
        "gasPrice": web3.toWei(wallet["buy_gwei"], "gwei"),
        "nonce": wallet["nonce"]
    })

    n = wallet
    n["nonce"] = str(int(wallet["nonce"]) + 1)

    helpers.update_wallet_by_id(wallet["id"], n)

    signed_transaction = web3.eth.account.sign_transaction(
        transaction, private_key=wallet["private_key"]
    )

    transaction_address = web3.toHex(
        web3.eth.send_raw_transaction(signed_transaction.rawTransaction))

    print(transaction_address)
    
    
    #transaction and trade operations
        
# Approve


def approve_token(modes, ca):
    try:
        token = web3.toChecksumAddress(ca)
    except:
        print("Invalid address")
        return
    t = helpers.localtime()
    current_time = helpers.strftime("%H:%M:%S", t)
    print(current_time, "Approving wallets in modes:", modes, "with", ca)


    token_contract = web3.eth.contract(token, abi=token_abi)

    for mode in modes:
        t = threading.Thread(
            target=approve_token_from_mode(mode, token, token_contract))
        t.start()


def approve_token_from_mode(mode, token, token_contract):
    wallets = mode.wallets

    for wallet in wallets:
        t = threading.Thread(target=approve_token_from_wallet(
            wallet, token, token_contract))


def approve_token_from_wallet(wallet, token, token_contract):
    if(not token in wallet.approved_tokens):
        address = wallet.address
        max_amount = web3.toWei(2**64-1, "ether")

        transaction = token_contract.functions.approve(pancake_router_contract_address, max_amount).buildTransaction({
            "from": address,
            "gasPrice": web3.toWei("12", "gwei"),
            "nonce": wallet.nonce
        })

        n = wallet
        n["nonce"] = str(int(wallet["nonce"]) + 1)

        helpers.update_wallet_by_id(wallet["id"], n)

        signed_transaction = web3.eth.account.sign_transaction(
            transaction, private_key=wallet.private_key
        )

        transaction_address = web3.toHex(
            web3.eth.send_raw_transaction(signed_transaction.rawTransaction))

        print(transaction_address)

    print("Token is already approved.")


# Sell
def sell_token(modes, ca):
    try:
        token = web3.toChecksumAddress(ca)
    except:
        print("Invalid address")
        return
    t = helpers.localtime()
    current_time = helpers.strftime("%H:%M:%S", t)
    print(current_time, "Selling", ca, "from wallets in modes:", modes)
    token_contract = web3.eth.contract(token, abi=token_abi)

    for mode in modes:
        t = threading.Thread(
            target=sell_token_from_mode(mode, token, token_contract))
        t.start()


def sell_token_from_mode(mode, token, token_contract):
    wallets = mode["wallets"]

    for wallet in wallets:
        t = threading.Thread(target=sell_token_from_wallet(
            wallet, token, token_contract))


def sell_token_from_wallet(wallet, token, token_contract):

    address = wallet["address"]

    balance = token_contract.functions.balanceOf(address).call()
    amount = web3.toWei(balance, "wei")
    if(amount > 0):
        transaction = pancake_contract.functions.swapExactTokensForETH(
            amount, 0,
            [token, wbnb],
            address,
            (int(helpers.time()) + 1000000)
        ).buildTransaction({
            "from": address,
            "gasPrice": web3.toWei(wallet["sell_gwei"], "gwei"),
            "gas": wallet["gas_limit"],
            "nonce": wallet["nonce"]
        })

        n = wallet
        n["nonce"] = str(int(wallet["nonce"]) + 1)

        helpers.update_wallet_by_id(wallet["id"], n)

        signed_transaction = web3.eth.account.sign_transaction(
            transaction, private_key=wallet["private_key"]
        )

        transaction_address = web3.toHex(
            web3.eth.send_raw_transaction(signed_transaction.rawTransaction))

        print(transaction_address)
    print("Token balance is already 0.")


def get_nonce(address):
    try:
        return web3.eth.get_transaction_count(address)
    except Exception as e:
        print(e)
        
def get_status(trade, tx_address):
    try:
        receipt = web3.eth.getTransactionReceipt(tx_address)
        # value =  web3.eth.getTransaction(trade.tx_address)
        # print(value)
        if(receipt):
            if(receipt.status == 1):
                trade.status = "Success"
            elif(receipt.status == 0):
                trade.status = "Fail"
        else:
            trade.status = "Pending"

        return trade.status
    except:
        raise f"Exception occured while getting status of the transaction {tx_address}. \nCheck transaction_helpers.py > get_status()."
    
